import React, { useState } from 'react';
import './style.css';
import dark from '../images/bg-desktop-dark.jpg';
import sun from '../images/icon-sun.svg';
import Footer from './Footer';
import Container from './Container';

import Circle from './Circle';
import cross from '../images/icon-cross.svg';



function App() {


  const [inputText, setInputText] = useState("")
  function handleChange(event) {
     const newValue = event.target.value
     setInputText(newValue)
  }
  function addItem() {
     setItems((prevItems) =>{
         return [...prevItems, inputText]
     })
     setItems("")
  }



  const [items , setItems] = useState([])
  const [done, setDone] = useState(false)

  function handleClick() {
    setDone((prevValue) =>{
      return !prevValue
    })
  }

  return (
    <div className="App">
      <div className='bg'><img src={dark} /></div>
      <div className='black'></div>
      <div className='box'>
        <div className='flex todo'><h1>TODO</h1><img src={sun} /></div>
        <div className='flex inp'>
            <div><input type="text" placeholder='Create a new todo...' value={inputText} onChange={handleChange} /></div>
            <button onClick={addItem}>ADD</button>

        </div>
        <div className='ctn'>

        <div className='flex ctn1'>
        <div className='flex2'>
         <Circle onClick={handleClick}/>
         {items.map((todoItem) =>  (
            <Container text={todoItem} style={{textDecoration: done ? "line-through" : "none"}}/>
         ))}

        </div>
        <div><img src={cross}/></div>
      </div>

          <Footer />
        </div>
      </div>
    </div>
  );
}

export default App;
