// ... (previous imports)

function App() {
  const [inputText, setInputText] = useState('');
  const [items, setItems] = useState([]);

  function handleChange(event) {
    const newValue = event.target.value;
    setInputText(newValue);
  }

  function addItem() {
    setItems((prevItems) => {
      return [...prevItems, { text: inputText, done: false }];
    });
    setInputText('');
  }

  function handleClick(index) {
    setItems((prevItems) => {
      const newItems = [...prevItems];
      newItems[index].done = !newItems[index].done;
      return newItems;
    });
  }

  function handleRemove(index) {
    setItems((prevItems) => {
      const newItems = [...prevItems];
      newItems.splice(index, 1);
      return newItems;
    });
  }

  return (
    <div className="App">
      <div className='bg'><img src={dark} alt="dark background" /></div>
      <div className='black'></div>
      <div className='box'>
        <div className='flex todo'><h1>TODO</h1><img src={sun} alt="sun icon" /></div>
        <div className='flex inp'>
          <div><input type="text" placeholder='Create a new todo...' value={inputText} onChange={handleChange} /></div>
          <button onClick={addItem}>ADD</button>
        </div>
        <div className='ctn'>
          <div className='flex ctn1'>
            <div className='flex2'>
              {items.map((todoItem, index) => (
                <div key={index} className='flex2'>
                  <Circle onClick={() => handleClick(index)} />
                  <Container
                    text={todoItem.text}
                    style={{ textDecoration: todoItem.done ? 'line-through' : 'none' }}
                  />
                  <img src={cross} alt="cross icon" onClick={() => handleRemove(index)} />
                </div>
              ))}
            </div>
          </div>
          <Footer />
        </div>
      </div>
    </div>
  );
}

export default App;
