  const [isSelected, setSelected] = useState(false)
  function handleClick() {

         setSelected(!isSelected)
  }  

    return(
      <div className='flex ctn1'>
      <div className='flex2'>
      
         <div className={isSelected ? 'circle' : 'selected'} onclick={handleClick}></div>
          <span style={{textDecoration: "line-through"}}>
           {props.text}
          </span>
        
      </div>
      <img src={cross} /> 
      </div>
    )
}
