import React, { useState } from 'react';
import './style.css';
import dark from '../images/bg-desktop-dark.jpg';
import sun from '../images/icon-sun.svg';
import Footer from './Footer';
import Container from './Container';

import Circle from './Circle';
import cross from '../images/icon-cross.svg';



function App() {

  const [inputText, setInputText] = useState('');
  const [items, setItems] = useState([]);

  function handleChange(event) {
    const newValue = event.target.value;
    setInputText(newValue);
  }

  function addItem() {
    setItems((prevItems) => {
      return [...prevItems, { text: inputText, done: false }];
    });
    setInputText('');
  }

  // function handleClick(index) {
  //   setItems((prevItems) => {
  //     const newItems = [...prevItems];
  //     newItems[index].done = !newItems[index].done;
  //     return newItems;
  //   });
  // }

  // function handleRemove(index) {
  //   setItems((prevItems) => {
  //     const newItems = [...prevItems];
  //     newItems.splice(index, 1);
  //     return newItems;
  //   });
  // }

  return (
    <div className="App">
      <div className='bg'><img src={dark} alt="dark background" /></div>
      <div className='black'></div>
      <div className='box'>
        <div className='flex todo'><h1>TODO</h1><img src={sun} alt="sun icon" /></div>
        <div className='flex inp'>
          <div><input type="text" placeholder='Create a new todo...' value={inputText} onChange={handleChange} /></div>
          <button onClick={addItem}>ADD</button>
        </div>
        <div className='ctn'>
          <div className='flex ctn1'>
            <div className='flex2'>
            {items.map((todoItem) => {
              <div className='flex2'>
                  {/* // <Circle />
                  // <Container
                  //   text={todoItem.text}
                  //   style={{ textDecoration: todoItem.done ? 'line-through' : 'none' }}
                  // /
                  //  */}
                 <Circle />
                  <span>
                   {todoItem}
                  </span>
                <img src={cross} /> 
              </div>
              })}
            </div>
          </div>
          <Footer />
        </div>
      </div>
    </div>
  );
}


export default App;
