import React, { useState, useEffect } from 'react';
import './style.css';
import dark from '../images/bg-desktop-dark.jpg';
import sun from '../images/icon-sun.svg';
import Footer from './Footer';
import Container from './Container';

function App() {
  const [inputText, setInputText] = useState('');
  const [items, setItems] = useState([]);
  const [filter, setFilter] = useState('')


  function handleChange(event) {
    const newValue = event.target.value;
    setInputText(newValue);
  }

  function addItem() {
      setItems((prevItems) => {
        return [...prevItems, inputText];

      }); 
      setInputText('');

  }

  // function deleteItem(id) {
  //   setItems((prevItems) =>{
  //     return prevItems.filter((item, index) =>{
  //       return index !== id
  //     })
  //   })
  // }


  function handleFilter(filter) {

    setFilter(filter)
  }

  if (filter === 'Completed') {
    console.log(items.filter((item) => item.isDone));
  } else if (filter === 'Active') {
    console.log(items.filter((item) => !item.isDone));
  } else {
    console.log(items);
  }




  return (
    <div className="App">
      <div className='bg'><img src={dark} alt="dark background" /></div>
      <div className='black'></div>
      <div className='box'>
        <div className='flex todo'><h1>TODO</h1><img src={sun} alt="sun icon" /></div>
        <div className='flex inp'>
          <div><input type="text" placeholder='Create a new todo...' value={inputText} onChange={handleChange} /></div>
          <button onClick={addItem}>ADD</button>
        </div>
        <div className='ctn'>
          {items.map((todoItem, index) => (
            <Container key={index} id={index} text={todoItem} />
          ))}
          <div className='flex last'>
            <div><p><span>5</span> items left</p></div>
            <div className='flex2'>
              <p onClick={() => onFilter('All')}>All</p>
              <p onClick={() => onFilter('Active')}>Active</p>
              <p onClick={() => onFilter('Completed')}>Completed</p>
            </div>
            <div><p>Clear Completed</p></div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
