import React, { useState } from 'react';
import './style.css';

export default function Input(addTask) {
    const [task, setTask] = useState('')
 
    console.log(task)
    function handleInput(event) {
        setTask(event.target.value)
    }

    function handlAdd(event) {
        event.prevent
        addTask(task)
    }

    return (
        <form onSubmit={handlAdd}>
        <div className='flex inp'>
            <div><input type="text" placeholder='Create a new todo...' onChange={handleInput} /></div>
            <button>ADD</button></div>
            </form>
    )
}
