import React, { useState, useEffect } from 'react';
import './style.css';
import dark from '../images/bg-desktop-dark.jpg';
import sun from '../images/icon-sun.svg';
import Footer from './Footer';
import Container from './Container';





function App() {

  const [inputText, setInputText] = useState('');
  const [items, setItems] = useState([]);

  useEffect(() => {
    localStorage.setItem('todoItems', JSON.stringify(items));
  }, [items]);

  useEffect(() => {
    const savedItems = JSON.parse(localStorage.getItem('todoItems')) || [];
    setItems(savedItems);
  }, []);
  function handleChange(event) {
    const newValue = event.target.value;
    setInputText(newValue);
  }

  function addItem() {
    setItems((prevItems) => {
      return [...prevItems, inputText];
    });
    setInputText('');
  }

  // function handleClick(index) {
  //   setItems((prevItems) => {
  //     const newItems = [...prevItems];
  //     newItems[index].done = !newItems[index].done;
  //     return newItems;
  //   });
  // }

  // function handleRemove(index) {
  //   setItems((prevItems) => {
  //     const newItems = [...prevItems];
  //     newItems.splice(index, 1);
  //     return newItems;
  //   });
  // }

  return (
    <div className="App">
      <div className='bg'><img src={dark} alt="dark background" /></div>
      <div className='black'></div>
      <div className='box'>
        <div className='flex todo'><h1>TODO</h1><img src={sun} alt="sun icon" /></div>
        <div className='flex inp'>
          <div><input type="text" placeholder='Create a new todo...' value={inputText} onChange={handleChange} /></div>
          <button onClick={addItem}>ADD</button>
        </div>
        <div className='ctn'>
        {items.map((todoItem) => 
          <Container 

          text={todoItem}/>
              )}

          <Footer />
        </div>
      </div>
    </div>
  );
}


export default App;
