import React, { useState } from 'react';
import './style.css';
import dark from '../images/bg-desktop-dark.jpg';
import sun from '../images/icon-sun.svg';
import Footer from './Footer';
import Container from './Container';




function App() {
const [task, setTask] = useState()

  return (
    <div className="App">
      <div className='bg'><img src={dark} /></div>
      <div className='black'></div>
      <div className='box'>
        <div className='flex todo'><h1>TODO</h1><img src={sun} /></div>
        <div className='flex inp'><div><input type="text" placeholder='Create a new todo...' /></div><button>ADD</button></div>
        <div className='ctn'>

          <Container />
          <Container />
          <Container />

          <Footer />
        </div>
      </div>
    </div>
  );
}

export default App;
