import React, { useState, useEffect } from 'react';
import './style.css';
import dark from '../images/bg-desktop-dark.jpg';
import sun from '../images/icon-sun.svg';
import Footer from './Footer';
import Container from './Container';

function App() {
  const [inputText, setInputText] = useState('');
  const [items, setItems] = useState([]);



  function handleChange(event) {
    const newValue = event.target.value;
    setInputText(newValue);
  }

  function addItem() {
    if (inputText.trim() !== '') {
      setItems((prevItems) => [...prevItems, { text: inputText, isDone: false }]);
      setInputText('');
    }
  }

  function deleteItem(id) {
    setItems((prevItems) =>{
      return prevItems.filter((item, index) =>{
        return index !== id
      })
    })
  }
  const [filter, setFilter] = useState("All")


  let filteredItems = items;
  if (filter === 'completed') {
    filteredItems = items.filter((item) => item.isDone);
  } else if (filter === 'active') {
    filteredItems = items.filter((item) => !item.isDone);
  }


  return (
    <div className="App">
      <div className='bg'><img src={dark} alt="dark background" /></div>
      <div className='black'></div>
      <div className='box'>
        <div className='flex todo'><h1>TODO</h1><img src={sun} alt="sun icon" /></div>
        <div className='flex inp'>
          <div><input type="text" placeholder='Create a new todo...' value={inputText} onChange={handleChange} /></div>
          <button onClick={addItem}>ADD</button>
        </div>
        <div className='ctn'>
          {items.map((todoItem, index) => (
            <Container key={index} id={index}text={todoItem.text} isDone={todoItem.isDone} onChecked={deleteItem} />
          ))}
          <Footer />
        </div>
      </div>
    </div>
  );
}

export default App;
